-- Esquema y datos para juego tipo Akinator (Dragon Ball)
-- Generado automáticamente desde src/models/data.json

USE akinator;

SET FOREIGN_KEY_CHECKS=0;
DROP TABLE IF EXISTS respuestas_partida;
DROP TABLE IF EXISTS personaje_pregunta;
DROP TABLE IF EXISTS preguntas;
DROP TABLE IF EXISTS partidas;
DROP TABLE IF EXISTS personajes;
SET FOREIGN_KEY_CHECKS=1;

CREATE TABLE personajes (
  id INT PRIMARY KEY,
  nombre VARCHAR(255) NOT NULL,
  descripcion TEXT,
  imagen_url VARCHAR(512),
  race VARCHAR(64),
  gender VARCHAR(32),
  affiliation VARCHAR(128),
  ki VARCHAR(64),
  maxKi VARCHAR(64)
 ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE preguntas (
  id INT PRIMARY KEY,
  texto_pregunta VARCHAR(255) NOT NULL,
  tipo VARCHAR(32) NOT NULL,
  opciones_json TEXT NULL
 ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE personaje_pregunta (
  personaje_id INT NOT NULL,
  pregunta_id INT NOT NULL,
  respuesta_esperada VARCHAR(128) NOT NULL,
  PRIMARY KEY (personaje_id, pregunta_id),
  CONSTRAINT fk_pp_personaje FOREIGN KEY (personaje_id) REFERENCES personajes(id) ON DELETE CASCADE,
  CONSTRAINT fk_pp_pregunta FOREIGN KEY (pregunta_id) REFERENCES preguntas(id) ON DELETE CASCADE
 ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE partidas (
  id INT AUTO_INCREMENT PRIMARY KEY,
  usuario_id INT NULL,
  personaje_objetivo_id INT NULL,
  estado ENUM('in_progress','completed') NOT NULL DEFAULT 'in_progress',
  estado_json JSON NULL,
  fecha TIMESTAMP DEFAULT CURRENT_TIMESTAMP
 ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE respuestas_partida (
  id INT AUTO_INCREMENT PRIMARY KEY,
  partida_id INT NOT NULL,
  pregunta_id INT NOT NULL,
  respuesta_usuario VARCHAR(128) NOT NULL,
  fecha TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  CONSTRAINT fk_rp_partida FOREIGN KEY (partida_id) REFERENCES partidas(id) ON DELETE CASCADE,
  CONSTRAINT fk_rp_pregunta FOREIGN KEY (pregunta_id) REFERENCES preguntas(id) ON DELETE CASCADE
 ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;





